import React, { useContext } from "react";
import { getMovies, getMovieById } from "../api/movies-api";
import PageTemplate from '../components/templateMovieListPage';
import { useQuery } from 'react-query';
import Spinner from '../components/spinner';
import AddToFavoritesIcon from '../components/cardIcons/addToFavorites'
import { useParams } from 'react-router-dom';
import { MoviesContext } from "../contexts/moviesContext";

const RecommendPage = (props) => {

  const context = useContext(MoviesContext);
  const { page } = useParams();
  const { data: pages, error, isLoading, isError } = useQuery(
    ["RecommendPage"],
    getMovies
  );
  if (isLoading) {
    return <Spinner />
  }

  if (isError) {
    return <h1>{error.message}</h1>
  }
  const movies = pages;

  const favorites = movies.filter(m => m.favorite)
  localStorage.setItem('favorites', JSON.stringify(favorites))

  let favoriteGneres = []

  context.favorites.map((json) => {
    getMovieById(json)
      .then((json) => {
        json.genres.map((response) => {
          if (!favoriteGneres.includes(response)) {
            favoriteGneres = [...favoriteGneres, response]
          }
        })
      }
      )
  })

  const compare = (() => {
    function compareArray(a, b) {
      if (a.length !== b.length) {
        return false;
      }
      const length = a.length;
      for (let i = 0; i < length; i++) {
        if (!compare(a[i], b[i])) {
          return false;
        }
      }

      return true;
    }

    function compareObject(a, b) {
      const keya = Object.keys(a);
      const keyb = Object.keys(b);

      if (keya.length !== keyb.length) {
        return false;
      }

      return keya.every(key => {
        if (!compare(a[key], b[key])) {
          return false;
        }
        return true;
      });
    }

    function compare(a, b) {
      if (a === b) {
        return true;
      }

      if (typeof a !== typeof b || a === null || b === null) {
        return false;
      }

      if (Array.isArray(a)) {
        if (!Array.isArray(b)) {
          return false;
        }
        return compareArray(a, b);
      }

      if (typeof a === "object") {
        return compareObject(a, b);
      }

      return false;
    }

    return compare;
  })();

  movies.filter((movie) => {
      compare(favoriteGneres,movie.genres)
      console.log()
  })

  return (
    <>
      <PageTemplate
        title="Recommend Movies"
        movies={movies}
        action={(movie) => {
          return <AddToFavoritesIcon movie={movie} />
        }}
      />
    </>
  );
};
export default RecommendPage;
